mapper.select:
SELECT * FROM getmessage_view

	userNumber = #{userNumber}

	AND readstatus = #{readStatus}

SELECT * FROM getmessage_view

	messageCode = #{messageCode}

	AND readstatus = #{readStatus}

mapper.insert:
	INSERT INTO messages
    VALUES(
    messages_seq.nextval,
    #{recvUser}
    , #{sendUser}
    , #{message}
    , 0
    , localtimestamp
    , #{notify})
