mapper.select:
SELECT COUNT(*) FROM getmessage_view
userNumber = #{userNumber}
AND sendyear||'-'||sendmonth||'-'||sendday like '%'||#{keyword}||'%' OR userNumber = #{userNumber} AND senduser like '%'||#{keyword}||'%' OR userNumber = #{userNumber} AND message like '%'||#{keyword}||'%'

SELECT * FROM (SELECT ROWNUM rnum, messagecode, usernumber, senduser, message, readstatus , sendyear, sendmonth, sendday, sendhour, sendmin, sendsec , day, hour, min, sec, notifystatus, senduserimage FROM getmessage_view
userNumber = #{userNumber}
AND sendyear||'-'||sendmonth||'-'||sendday like '%'||#{keyword}||'%' OR userNumber = #{userNumber} AND senduser like '%'||#{keyword}||'%' OR userNumber = #{userNumber} AND message like '%'||#{keyword}||'%'
)
WHERE rnum >= #{start} AND rnum <= #{end}
