mapper.select:
select * from (select * from lectureview
grade like '%'||#{keyword}||'%' OR lecCode like '%'||#{keyword}||'%' OR lectureName like '%'||#{keyword}||'%' OR lectureScore like '%'||#{keyword}||'%' OR accountHolder like '%'||#{keyword}||'%' OR classTime like '%'||#{keyword}||'%' OR maximumCapacity like '%'||#{keyword}||'%'
OR majorName like '%'||#{major}||'%'
)
WHERE rNum >= #{start} and rNum <= #{end}


 	SELECT *  
				FROM (select SCHOLARPK,SCHOLARNAME,to_char(YEAR,'YYYY') as YEAR,SEMESTER,AMOUNT,ROWNUM rNum  
				            from ( 
				                    select * FROM SCHOLARSHIP   
				                    order by SCHOLARPK desc
				                    ) 
				                ) 
				                where rNum >= #{start} and rNum <= #{end}
