mapper.select:
select * from (select * from lectureview
grade||'학년' like '%'||#{keyword}||'%' OR lecCode like '%'||#{keyword}||'%' OR lectureName like '%'||#{keyword}||'%' OR lectureScore like '%'||#{keyword}||'%' OR accountHolder like '%'||#{keyword}||'%' OR classTime like '%'||#{keyword}||'%' OR maximumCapacity like '%'||#{keyword}||'%'
)
WHERE rNum >= #{start} and rNum <= #{end}
